import { DDModalClient } from '../modal/modal';
import { DDNotificationClient } from '../notification/notification';
import { DDSidePanelClient } from '../side-panel/side-panel';
import type {
    ClientContext,
    ClientOptions,
    Context,
    ContextClient,
    DebugClient,
    EventClient,
    EventHandler,
    IFrameDimensions,
    LoggerClient,
    ParentAuthStateOptions,
    RequestClient,
    RequestHandler
} from '../types';
