import { LoadedResourceMetadata, LoadedResourceMetaDataBatch } from '../types';

export type LoadedResourceIds = Set<string>;

const isPerformanceObjectSupported = () => {
    return window.performance !== undefined && 'getEntries' in performance;
};

const isResourceTimingEntry = (
    entry: PerformanceEntry
): entry is PerformanceResourceTiming => {
    return entry.entryType === 'resource';
};

const isRequestKind = (timing: PerformanceResourceTiming) => {
    return (
        timing.initiatorType === 'xmlhttprequest' ||
        timing.initiatorType === 'fetch'
    );
};

export const parseResource = (
    r: PerformanceResourceTiming
): LoadedResourceMetadata => {
    // safe because r.name represents the resolved URL of the requested resource.
    const urlHostname = new URL(r.name).hostname;
    const ts = performance.timeOrigin;
    let url;
    if (!isRequestKind(r)) {
        // for privacy reason we do not capture full URLs of network calls
        url = r.name;
    }
    return {
        startTimeTs: ts + r.startTime,
        secureConnectionStartTs: ts + r.secureConnectionStart,
        url,
        urlHostname,
        initiatorType: r.initiatorType,
        nextHopProtocol: r.nextHopProtocol,
        duration: r.duration,
        decodedBodySize: r.decodedBodySize
    };
};

const collectInitialResources = () =>
    performance
        .getEntriesByType('resource')
        .filter(isResourceTimingEntry)
        .map(parseResource);

export const startResourceMonitoring = (
    reportUsage: (batch: LoadedResourceMetaDataBatch) => void
): (() => void) => {
    if (!isPerformanceObjectSupported()) {
        return () => {};
    }

    // first, report all previously loaded resources
    reportUsage({ resources: collectInitialResources() });

    // now, set up observer for all new resources
    const observerCallback = (entries: PerformanceObserverEntryList) => {
        const resources = entries
            .getEntries()
            .filter(isResourceTimingEntry)
            .map(parseResource);

        reportUsage({ resources });
    };

    const observer = new PerformanceObserver(observerCallback);
    observer.observe({ type: 'resource', buffered: true });

    return () => {
        observer.disconnect();
    };
};
import { EventType, FeatureType, enabledEvents } from '../constants';
import { features } from '../features';
import type { DefinitionWithKey } from '../types';

const memoize = <T>(getter: () => T): (() => T) => {
    let executed = false;
    let value: T;

    return () => {
        if (!executed) {
            value = getter();
            executed = true;
        }

        return value;
    };
};

export const isFeatureEnabled = (
    feature: FeatureType,
    enabledFeatures: FeatureType[]
): boolean => enabledFeatures.includes(feature);

export const getFeatureTypesByEvent = memoize(
    (): Map<EventType, Set<FeatureType>> => {
        const featureTypesByEvent = new Map<EventType, Set<FeatureType>>();

        features.forEach(feature => {
            feature.events.forEach((e: EventType) => {
                if (!featureTypesByEvent.has(e)) {
                    featureTypesByEvent.set(e, new Set());
                }

                featureTypesByEvent.get(e)!.add(feature.type);
            });
        });

        return featureTypesByEvent;
    }
);

export const isEventEnabled = (
    event: EventType,
    enabledFeatures: FeatureType[]
): boolean => {
    if (enabledEvents.has(event)) {
        return true;
    }

    const featureTypesByEvent = getFeatureTypesByEvent();

    // get the set of features that enable this event
    const enablingFeatures = featureTypesByEvent.get(event as EventType);

    // if no enabling feature found, event is unknown
    if (!enablingFeatures) {
        return false;
    }

    return enabledFeatures.some(feature => enablingFeatures.has(feature));
};

export const isDefinitionWithKey = (
    defenition: any
): defenition is DefinitionWithKey => !!defenition.key;

export const validateKey = <T = any>(definition: T): boolean => {
    if (!isDefinitionWithKey(definition)) {
        throw new Error('Definition missing required field ".key"');
    }

    return true;
};

export const setImmediateInterval = (
    callback: () => unknown,
    timeout: number
) => {
    callback();

    return setInterval(callback, timeout);
};
